"use strict";

// Мобильное меню
var navToggleItems = document.querySelectorAll('.js-nav-toggle');
var nav = document.querySelector('.nav');
navToggleItems.forEach(function (e) {
  e.addEventListener('click', function () {
    nav.classList.toggle('nav_opened');
  });
});

// Поиск
var searchToggleItems = document.querySelectorAll('.js-search-toggle');
var search = document.querySelector('.search__wrap');
searchToggleItems.forEach(function (e) {
  e.addEventListener('click', function () {
    search.classList.toggle('search__wrap_opened');
  });
});

// Валидация форм
var subscribeForm = new JustValidate('#subscribe-form', {
  errorFieldCssClass: 'form__input_dark-invalid',
  errorFieldStyle: {},
  errorLabelCssClass: 'form__input_dark-alert form__input-alert',
  errorLabelStyle: {},
  focusInvalidField: true,
  lockForm: true
});
subscribeForm.addField('#email', [{
  rule: 'required',
  errorMessage: 'Поле обязательно для заполнения'
}, {
  rule: 'email',
  errorMessage: 'Недопустимый формат'
}]);
var contactsForm = new JustValidate('#contacts-form', {
  errorFieldCssClass: 'form__input_light-invalid',
  errorFieldStyle: {},
  errorLabelCssClass: 'form__input_light-alert form__input-alert',
  errorLabelStyle: {},
  focusInvalidField: true,
  lockForm: true
});
contactsForm.addField('#name', [{
  rule: 'required',
  errorMessage: 'Поле обязательно для заполнения'
}]).addField('#email', [{
  rule: 'required',
  errorMessage: 'Поле обязательно для заполнения'
}, {
  rule: 'email',
  errorMessage: 'Недопустимый формат'
}]);
"use strict";

ymaps.ready(function () {
  var map = new ymaps.Map('map', {
      center: [55.76001699344121, 37.614238554747374],
      zoom: 14,
      controls: []
    }, {
      searchControlProvider: 'yandex#search'
    }),
    objectMarker = new ymaps.GeoObject({
      // Описание геометрии.
      geometry: {
        type: "Point",
        coordinates: [55.76963601332982, 37.636710499999985]
      }
    }, {
      // Опции.
      // Необходимо указать данный тип макета.
      iconLayout: 'default#image',
      // Своё изображение иконки метки.
      iconImageHref: 'images/map-marker.png',
      // Размеры метки.
      iconImageSize: [12, 12],
      // Смещение левого верхнего угла иконки относительно
      // её "ножки" (точки привязки).
      iconImageOffset: [0, 0]
    }, {
      // Опции.
      // Иконка метки будет растягиваться под размер ее содержимого.
      preset: 'islands#blackStretchyIcon',
      // Метку можно перемещать.
      draggable: true
    });
  map.geoObjects.add(objectMarker);
  map.behaviors.disable('scrollZoom');
});
